INSERT INTO `cadeaux` VALUES (30001,'uploads/week-end-insolite.jpg','Bongo FR Week-end Insolite','Bongo','&lt;p&gt;Quoi de plus efficace pour &amp;eacute;chapper &amp;agrave; la routine qu&amp;rsquo;une &amp;eacute;chapp&amp;eacute;e pleine d&amp;rsquo;originalit&amp;eacute; ? Gr&amp;acirc;ce &amp;agrave; ce coffret cadeau Bongo S&amp;eacute;jour insolite, deux &amp;acirc;mes en qu&amp;ecirc;te d&amp;rsquo;&amp;eacute;vasion pourront profiter d&amp;rsquo;une nuit avec petit-d&amp;eacute;jeuner, &amp;agrave; vivre dans le cadre surprenant d&amp;rsquo;une cabane, d&amp;rsquo;une roulotte, d&amp;rsquo;un bateau ou encore d&amp;rsquo;un h&amp;ocirc;tel &amp;agrave; th&amp;egrave;me. Id&amp;eacute;al pour d&amp;eacute;connecter en duo !&lt;/p&gt;',99.90,1,3),(30002,'uploads/aventure-en-duo.jpg','Bongo FR Aventure en Duo','Bongo','&lt;p&gt;Ce ch&amp;egrave;que-cadeau donne droit &amp;agrave; l&#039;une des activit&amp;eacute;s propos&amp;eacute;es par les organisateurs s&amp;eacute;lectionn&amp;eacute;s.&lt;/p&gt;',39.90,1,3),(30003,'uploads/cinema.jpg','Bongo FR Cin&eacute;ma &amp; Pop-corn &agrave; deux','Bongo','&lt;p&gt;Bongo Giftbox Cin&amp;eacute;ma &amp;amp; Pop-corn &amp;agrave; deux&lt;/p&gt;',34.90,1,3),(30004,'uploads/gastronomie-et-vin.jpg','Bongo FR Gastronomie et Vin','Bongo','&lt;p&gt;Ce Bongo donne droit &amp;agrave; un diner gastronomique pour deux personnes (formule vin compris), &amp;agrave; d&amp;eacute;guster dans l&#039;un des restaurants s&amp;eacute;lectionn&amp;eacute;s.&lt;/p&gt;',119.90,1,3),(30005,'uploads/jours-de-charme.jpg','Bongo FR 2 Jours de Charme','Bongo','&lt;p&gt;Bongo FR 2 Jours de Charme&lt;/p&gt;',74.90,1,3),(30006,'uploads/joyeux-anniversaire.jpg','Bongo FR Joyeux Anniversaire','Bongo','&lt;p&gt;Joyeux Anniversaire&lt;/p&gt;',29.90,1,3),(30007,'uploads/moments-de-bien-etre.jpg','Bongo FR Moments de bien-&ecirc;tre','Bongo','&lt;p&gt;Bongo Giftbox Moments de bien-&amp;ecirc;tre&lt;/p&gt;',49.90,1,3),(30008,'uploads/tables-romantiqu